import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import ApiClient from "../../services/apiClient.JS";
import "./RegisterDoctor.css";

const RegisterDoctor = ({ loginHandler, userUpdater }) => {
  //useState variables for the individual input types
  const [email, setEmail] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [registrationNumber, setRegistrationNumber] = useState("");

  // useState variables for the registration errors

  const [registrationError, setRegistrationError] = useState(null);

  //navigation for redirecting once registration
  let navigate = useNavigate();

  //submit handler for the form
  const handleRegistration = async (e) => {
    e.preventDefault();

    const registrationInfo = JSON.stringify({
      email,
      firstName,
      lastName,
      username,
      registrationNumber,
      password,
    });

    const { data, error } = await ApiClient.registerDoctor(registrationInfo);

    if (error) {
      setRegistrationError(error);
    }

    if (data?.user) {
      userUpdater(data.user);
      ApiClient.setToken(data.token);
      loginHandler();
      navigate("/");
    }
  };

  return (
    <div>
      <div className="screen-split">
        <div className="left-side">
          <section className="copy-reg">
            <h1 className="header-reg">Register</h1>
            <p>Users awaiting your trustful advice</p>
          </section>
        </div>
        <div className="right-side">
          <form className="audio-form" onSubmit={handleRegistration}>
            <section className="copy-reg">
              <h2 className="register-header">Audiologist Registration</h2>
              <img
                src="data:image/png;base64,"
                alt="register icon"
              />
            </section>
            <div className="all-inputs">
              <div className="reg-user-pass">
                <div className="audio-inputs">
                  <input
                    className="form-input1"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    placeholder="Email"
                  />
                </div>
              </div>

              <div className="reg-user-pass">
                <div className="audio-inputs">
                  <input
                    className="form-input1"
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    placeholder="Username"
                  />
                </div>
              </div>

              <div className="reg-user-pass">
                <div className="audio-inputs-special">
                  <input
                    className="form-input1"
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                    placeholder="First Name"
                  />

                  <input
                    className="form-input1"
                    type="text"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    required
                    placeholder="Last Name"
                  />
                </div>
              </div>

              <div className="reg-user-pass">
                <div className="audio-inputs">
                  <input
                    className="form-input1"
                    type="text"
                    value={registrationNumber}
                    onChange={(e) => setRegistrationNumber(e.target.value)}
                    required
                    placeholder="Registration Number"
                  />
                </div>
              </div>

              <div className="reg-user-pass">
                <div className="audio-inputs">
                  <input
                    className="form-input1"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    placeholder="Password"
                  />
                </div>
              </div>
            </div>
            <div className="login-bottom">
              <button className="register-bttn" type="submit">
                Register
              </button>
              <p id="error-display-doctor">{registrationError}</p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RegisterDoctor;
